<script type="text/javascript">
	var scriptToLoad = ["//www.googleadservices.com/pagead/conversion_async.js"];

	scriptToLoad.forEach(function(file) {
	    var loader = new ScriptLoader();
	    loader.add(file)
	    loader.loaded(function(failedCallback) {
	        console.log("Failed to load Adwords remarketing tag.");
	    });
	});

	function ScriptLoader() {
	    var promises = [];

	    this.add = function(url) {
	        var promise = new Promise(function(resolve, reject) {
	            var script = document.createElement('script');
	            script.src = url;

	            script.addEventListener('load', function() {
	                resolve(script);
	            }, false);

	            script.addEventListener('error', function() {
	                reject(script);
	            }, false);

	            document.body.appendChild(script);
	        });
	        promises.push(promise);
	    };

	    this.loaded = function(callbackOnFailed) {
	        Promise.all(promises).then(function(result) {
	        	/* <![CDATA[ */
				window.google_trackConversion({
					google_conversion_id: 972177812,
					google_remarketing_only: true,
					google_custom_params: window.google_tag_params
				});
				/* ]]> */
	        }, callbackOnFailed);
	    };
	}
</script>
